const modal = (props) => {
    let showModal = (
        <Aux>
            <div 
                className={classes.Backdrop}
                onClick={clicked}></div>
            <div className={classes.Modal}>
                {props.children}
            </div>
        </Aux>
    )

    if (!props.showModal){
        showModal = null;
    }

    return (
        <Aux>
            {showModal}
        </Aux>
    )
}

export default modal;
