import React from 'react';

import classes from './Modal.module.css';

const modal = (props) => (
    <div 
        className={classes.Background}
        style={ props.show ? {display: 'none'} : {display: 'flex'}}>
        <div className={classes.Modal}>
            {props.children}
        </div>
    </div>
)

export default modal;
